import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import swal from 'sweetalert2';

const URI = "http://localhost:8000/recipes/";
const URI2 = "http://localhost:8000/categories/";

const CompShowRecipes = () => {

    const [recipes, setRecipes] = useState([]);
    const [categories, setCategories] = useState([]);

    useEffect( ()=>{
        getRecipes()
    },[])


    //Obtener Recetas
    const getRecipes = async () => {
        const res = await axios.get(URI);
        setRecipes(res.data);
    }

    //Obtener Categorias
    const getCategories = async () => {
        const res = await axios.get(URI2);
        setCategories(res.data);
    }

    //Eliminar Receta
    const deleteRecipes = async (id) => {
        swal.fire({
            title: 'Estas seguro?',
            text: "No podras revertir esta accion!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33'
          }).then(async (result) => {
            if (result.isConfirmed) {
                await axios.delete(URI+id);
                swal.fire(
                    'Eliminado!',
                    'La receta ha sido eliminada.',
                    'success'
                )
                getRecipes();
            }
          })
    }

    return (
        <div>
            <h1>Lista de recetas</h1>
            <Link to ={'/recipes/create'} className="btn btn-primary">Crear Receta</Link>
            <table class="table">
                <thead>
                    <tr>
                    <th scope="col">Nombre</th>
                    <th scope="col">Ingredientes</th>
                    <th scope="col">Tiempo de preparacion</th>
                    <th scope="col">Instrucciones</th>
                    <th scope="col">Informacion nutricional</th>
                    <th scope="col">Categoria</th>
                    <th scope="col">Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {recipes.map(recipe => (
                        <tr>
                            <td>{recipe.nombre_receta}</td>
                            <td>{recipe.descripcion_receta}</td>
                            <td>{recipe.tiempo_preparacion}</td>
                            <td>{recipe.intrucciones}</td>
                            <td>{recipe.informacion_nutricional}</td>
                            <td>{recipe.fb_categoria.nombre_categoria}</td>
                            <td>
                                <Link to ={'/recipes/edit/'+recipe._id} className="btn btn-warning">Editar</Link>
                                <button onClick={()=>deleteRecipes(recipe._id)} className="btn btn-danger">Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )

}

export default CompShowRecipes;
