import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const URI = "http://localhost:8000/recipes/";

const CompShowRecipes = () => {

    const [recipes, setRecipes] = useState([]);
    useEffect( ()=>{
        getRecipes()
    },[])

    //Obtener Recetas
    const getRecipes = async () => {
        const res = await axios.get(URI);
        setRecipes(res.data);
    }

    //Eliminar Receta
    const deleteRecipes = async (id) => {
        await axios.delete(URI+id);
        getRecipes();
    }

    return (
        <div>
            <h1>Lista de recetas</h1>
            <Link to ={'/create'} className="btn btn-primary">Crear Receta</Link>

            {recipes.map(recipe => (
                <div className="card w-75 mb-3">
                    <div className="card-body">
                        <h5 className="card-title">{recipe.nombre_receta}</h5>
                        <p className="card-text">{recipe.descripcion_receta}</p>
                        <Link to={'/edit/'+recipe._id} className="btn btn-primary">Detalles</Link>
                    </div>
                </div>
            ))
            }
        </div>
    )

}

export default CompShowRecipes;
