import { Link } from 'react-router-dom';
import { useAuth } from '../AuthContext/AuthContext.js';
import { useState, useEffect } from 'react';
import axios from 'axios';

const Navbar = () => {
    const { logout } = useAuth();

    const user = JSON.parse(localStorage.getItem('userId'));
    const URI = "http://localhost:8000/users/"+user;
    const [users, setUsers] = useState("");

    useEffect(() => {
        const getUser = async () => {
            const res = await axios.get(URI);
            setUsers(res.data);
            console.log(res.data);
        }
        getUser();
    }, [URI]);

    return(
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
            <div className="container-fluid">
                <Link to ={'/dashboard'} className="navbar-brand">Navbar</Link>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav">
                        <li className="nav-item"><Link to ={'/users'} className="nav-link">Usuarios</Link></li>
                    </ul>
                    <ul className="navbar-nav">
                        <li className="nav-item"><Link to ={'/recipes'} className="nav-link">Recetas</Link></li>
                    </ul>
                    <ul className="navbar-nav">
                        <li className="nav-item"><Link to ={'/users/'+user} className="nav-link">Recetas favoritas</Link></li>
                    </ul>
                    <ul className="navbar-nav">
                        <li className="nav-item"><button onClick={logout}>Cerrar Sesi√≥n</button></li>
                    </ul>
                    <ul className='navbar-nav'>
                        <li className="nav-item"><p>{users.alias}</p></li>
                    </ul>
                </div>
            </div>
        </nav>
    )
    
}

export default Navbar;
