import axios from "axios";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import swal from 'sweetalert2';
import { useAuth } from "../AuthContext/AuthContext.js";
import './RecipesCss/CreateRecipe.css';

const URI = "http://localhost:8000/recipes/";
const URI2 = "http://localhost:8000/categories/";

const CompCreateRecipes = () => {
    
        const [nombre, setNombre] = useState('');
        const [descripcion, setDescripcion] = useState('');
        const [tiempo, setTiempo] = useState('');
        const [instrucciones, setInstrucciones] = useState('');
        const [informacion, setInformacion] = useState('');
        const [categorias, setCategoria] = useState([]);
        const [selectedCategoria, setSelectedCategoria] = useState('');
        const [mostrarSelect, setMostrarSelect] = useState(true);
        const [categoriaInput, setCategoriaInput] = useState('');
        const [descripcionCategoria, setDescripcionCategoria] = useState('');
        const navigate = useNavigate();
        const { authState } = useAuth();

        useEffect(() => {
            if (!authState.isAuthenticated) {
                navigate('/');
                return;
            }
        }, [authState.isAuthenticated, navigate]);

        useEffect( ()=>{
            getCategories()
        },[])

        //Obtener Categorias
        const getCategories = async () => {
            const res = await axios.get(URI2);
            setCategoria(res.data);
        }

    
        //Guardar Receta
    
        const store = async (e) => {
            e.preventDefault();

            const recipe = {
                nombre_receta: nombre,
                descripcion_receta: descripcion,
                tiempo_preparacion: tiempo,
                intrucciones: instrucciones,
                informacion_nutricional: informacion,
                id_categoria: selectedCategoria 
            }

            if(!mostrarSelect){
                const categoria = {
                    nombre_categoria: categoriaInput,
                    descripcion_categoria: descripcionCategoria
                }
                const res = await axios.post(URI2, categoria);
                if(res.data.success){
                    recipe.id_categoria = res.data.id;
                    const result = await axios.post(URI, recipe);
                    if(!result.data.success){
                        return swal.fire({
                            title: 'Error!',
                            text: 'No se pudo guardar la receta',
                            icon: 'error',
                            confirmButtonText: 'OK'
                        })
                    }else{
                        return swal.fire({
                            title: 'Exito!',
                            text: result.data.message,
                            icon: 'success',
                            confirmButtonText: 'OK'
                        }).then((result) => {
                            if (result.isConfirmed) {
                                navigate('/recipes');
                            }
                        })
                    }
                }else{
                    return swal.fire({
                        title: 'Error!',
                        text: res.data.message,
                        icon: 'error',
                        confirmButtonText: 'OK'
                    })
                }
            }else{
                const result = await axios.post(URI, recipe);
                if(!result.data.success){
                    return swal.fire({
                        title: 'Error!',
                        text: 'No se pudo guardar la receta',
                        icon: 'error',
                        confirmButtonText: 'OK'
                    })
                }else{
                    return swal.fire({
                        title: 'Exito!',
                        text: result.data.message,
                        icon: 'success',
                        confirmButtonText: 'OK'
                    }).then((result) => {
                        if (result.isConfirmed) {
                            navigate('/recipes');
                        }
                    })
                }
            }
        }

        const aÃ±adirCategoria = async () => {
            setMostrarSelect(false);
        }

    return(
        <div className="backgroundCreateRecipe">
            <div className="compCreateRecipes">
                <h3>Registrar Receta</h3>
                <form onSubmit={store}>
                    <div className="mb-3">
                        <label>Nombre</label>
                        <input type="text" placeholder="Nombre..." className="form-control" onChange={(e)=>setNombre(e.target.value)} value={nombre} required/>
                    </div>
                    <div className="mb-3">
                        <label>Descripcion</label>
                        <input type="text" placeholder="DescripciÃ³n..." className="form-control" onChange={(e)=>setDescripcion(e.target.value)} value={descripcion} required/>
                    </div>
                    <div className="mb-3">
                        <label>Ingredientes</label>
                        <input type="text" placeholder="Ingredientes..." className="form-control" onChange={(e)=>setIngredientes(e.target.value)} value={descripcion} required/>
                    </div>
                    <div className="mb-3">
                        <label>Tiempo de preparacion (en minutos)</label>
                        <input type="number" placeholder="Tiempo de preparaciÃ³n..." className="form-control" onChange={(e)=>setTiempo(e.target.value)} value={tiempo} required/>
                    </div>
                    <div className="mb-3">
                        <label>Instrucciones</label>
                        <input type="text" placeholder="Instrucciones..." className="form-control" onChange={(e)=>setInstrucciones(e.target.value)} value={instrucciones} required/>
                    </div>
                    <div className="mb-3">
                        <label>Informacion nutricional</label>
                        <input type="text" placeholder="InformaciÃ³n nutricional..." className="form-control" onChange={(e)=>setInformacion(e.target.value)} value={informacion} required/>
                    </div>
                    <div className="mb-3">
                    <label>Categoria</label>
                    {mostrarSelect ? (
                        <select className="form-control" onChange={(e)=>setSelectedCategoria(e.target.value)}>
                            <option selected disabled>Seleccionar una categoria...</option>
                            {categorias.map(categoria => (
                                <option key={categoria.id} value={categoria.id}>{categoria.nombre_categoria}</option>
                            ))}
                        </select>
                        ) : (
                            <div>
                                <div className="mb-3">
                                    <input type="text" placeholder="Nueva categorÃ­a..." className="form-control" onChange={(e)=>setCategoriaInput(e.target.value)} value={categoriaInput} required/>
                                </div>
                                <div className="mb-3">
                                    <label>Descripcion de categoria</label>
                                    <input type="text" placeholder="DescripciÃ³n de categoria" className="form-control" onChange={(e)=>setDescripcionCategoria(e.target.value)} value={descripcionCategoria} required/>
                                </div>
                            </div>
                            
                        )}
                        <button type="button" className="btn btn-success" onClick={()=>aÃ±adirCategoria()}>AÃ±adir Categoria</button>
                    </div>
                    <button type="submit" className="btn btn-primary">Guardar</button>
                </form>
            </div>        
        </div>
    
    )

}

export default CompCreateRecipes;
