import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router";
import { Link } from "react-router-dom";
import swal from 'sweetalert2';

const URI = "http://localhost:8000/recipes/";
const URI2 = "http://localhost:8000/categories/";

const CompShowDashboard = () => {

    const [recipes, setRecipes] = useState([]);
    useEffect( ()=>{
        getRecipes()
    },[])

    //Obtener Recetas
    const getRecipes = async () => {
        const res = await axios.get(URI);
        setRecipes(res.data);
    }

    return(
        <div className="container-fluid">
            <h1>Dashboard</h1>
            <div className="recipesDashboard container">
                {recipes.map(recipe => (
                    <div className="card w-75 mb-3">
                        <div className="card-body">
                            <h5 className="card-title">{recipe.nombre_receta}</h5>
                            <p className="card-text">{recipe.descripcion_receta}</p>
                            <Link to={'/recipe/:id'} className="btn btn-primary">Detalles</Link>
                            {/* <a href="#" className="btn btn-primary">Button</a> */}
                        </div>
                    </div>

                ))}
            </div>
        </div>
    )
}

export default CompShowDashboard;
