import { Link } from 'react-router-dom';
import { useAuth } from '../AuthContext/AuthContext.js';
import { useEffect } from 'react';

const Navbar = () => {

    const { logout, authState } = useAuth();

    useEffect(() => {
        if (!authState.isAuthenticated) {
            return;
        }
    }, [authState.isAuthenticated]);

    const user = JSON.parse(localStorage.getItem('user'));

    return(
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
            <div className="container-fluid">
                <Link to ={'/dashboard'} className="navbar-brand">Navbar</Link>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav">
                        <li className="nav-item"><Link to ={'/users'} className="nav-link">Usuarios</Link></li>
                    </ul>
                    <ul className="navbar-nav">
                        <li className="nav-item"><Link to ={'/recipes'} className="nav-link">Recetas</Link></li>
                    </ul>
                    <ul className="navbar-nav">
                        <li className="nav-item"><Link to ={'/users/'+user.id} className="nav-link">Recetas favoritas</Link></li>
                    </ul>
                    <ul className="navbar-nav">
                        <li className="nav-item"><button onClick={logout}>Cerrar Sesi√≥n</button></li>
                    </ul>
                </div>
            </div>
        </nav>
    )
    
}

export default Navbar;
