import axios from "axios";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";

const URI = "http://localhost:8000/users/";
const URIComplement = "/recipes";

const CompShowPersonalRecipes = () => {

    const {id} = useParams();
    const [recetas, setRecetas] = useState([]);


    //Obtener Recetas por el ID de usuario
    useEffect( ()=>{
        const getRecipesByUserId = async () => {
            const res = await axios.get(URI+id+URIComplement);
            console.log(res.data[0].fb_receta);
            setRecetas(res.data);
        }

        getRecipesByUserId();
    },[id])


    return(
        <div>
            <h1>Recetas Personales</h1>
            <div className="recipesDashboard">
                {recetas.length === 0 ? (
                    <div className="alert alert-info" role="alert">
                        No tienes recetas personales
                    </div>
                ) : (
                    recetas.map(recipe => (
                        <div className="card w-75 mb-3" key={recipe.id}>
                            <div className="card-body">
                                <h5 className="card-title">{recipe.nombre_receta}</h5>
                                <p className="card-text">{recipe.descripcion_receta}</p>
                                <Link to={'/recipe/'+recipe.id} className="btn btn-primary">Detalles</Link>
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    )

}

export default CompShowPersonalRecipes;
