import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const URI = "http://localhost:8000/recipes/";

const CompShowRecipes = () => {

    const [recipes, setRecipes] = useState([]);
    useEffect( ()=>{
        getRecipes()
    },[])

    //Obtener Recetas
    const getRecipes = async () => {
        const res = await axios.get(URI);
        setRecipes(res.data);
    }

    //Eliminar Receta
    const deleteRecipes = async (id) => {
        await axios.delete(URI+id);
        getRecipes();
    }

    return (
        <div>
            <h1>Lista de recetas</h1>
            <Link to ={'/create'} className="btn btn-primary">Crear Receta</Link>

            <div class="card w-75 mb-3">
                <div class="card-body">
                    <h5 class="card-title">Card title</h5>
                    <p class="card-text">With supporting text below as a natural lead-in to additional content.</p>
                    <a href="#" class="btn btn-primary">Button</a>
                </div>
            </div>

            <table class="table">
                <thead>
                    <tr>
                    <th scope="col">Nombre</th>
                    <th scope="col">Ingredientes</th>
                    <th scope="col">Tiempo de preparacion</th>
                    <th scope="col">Instrucciones</th>
                    <th scope="col">Informacion nutricional</th>
                    <th scope="col">Categoria</th>
                    <th scope="col">Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {recipes.map(recipe => (
                        <tr>
                            <td>{recipe.nombre_receta}</td>
                            <td>{recipe.descripcion_receta}</td>
                            <td>{recipe.tiempo_preparacion}</td>
                            <td>{recipe.intrucciones}</td>
                            <td>{recipe.informacion_nutricional}</td>
                            <td>{recipe.fb_categoria.nombre_categoria}</td>
                            <td>
                                <Link to ={'/edit/'+recipe._id} className="btn btn-warning">Editar</Link>
                                <button onClick={()=>deleteRecipes(recipe._id)} className="btn btn-danger">Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )

}

export default CompShowRecipes;
