import axios from "axios";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import swal from 'sweetalert2';

const URI = "http://localhost:8000/recipes/";
const URI2 = "http://localhost:8000/categories/";

const CompCreateRecipes = () => {
    
        const [nombre, setNombre] = useState('');
        const [descripcion, setDescripcion] = useState('');
        const [tiempo, setTiempo] = useState('');
        const [instrucciones, setInstrucciones] = useState('');
        const [informacion, setInformacion] = useState('');
        const [categoria, setCategoria] = useState('');
        const navigate = useNavigate();

        //Obtener Categorias
        const [categories, setCategories] = useState([]);
        useEffect( ()=>{
            getCategories()
        },[])

    
        //Guardar Receta
        const recipe = {
            nombre_receta: nombre,
            descripcion_receta: descripcion,
            tiempo_preparacion: tiempo,
            intrucciones: instrucciones,
            informacion_nutricional: informacion,
            fb_categoria: categoria
        }
    
        const store = async (e) => {
            e.preventDefault();
            const result = await axios.post(URI, recipe);
            if(!result.data.success){
                return swal.fire({
                    title: 'Error!',
                    text: result.data.message,
                    icon: 'error',
                    confirmButtonText: 'OK'
                })
            }else{
                return swal.fire({
                    title: 'Exito!',
                    text: result.data.message,
                    icon: 'success',
                    confirmButtonText: 'OK'
                }).then((result) => {
                    if (result.isConfirmed) {
                        navigate('/recipes');
                    }
                })
            }
        }

    return(

        <div>
            <h3>Registrar Receta</h3>
            <form onSubmit={store}>
                <div className="mb-3">
                    <label>Nombre</label>
                    <input type="text" className="form-control" onChange={(e)=>setNombre(e.target.value)} value={nombre}/>
                </div>
                <div className="mb-3">
                    <label>Descripcion</label>
                    <input type="text" className="form-control" onChange={(e)=>setDescripcion(e.target.value)} value={descripcion}/>
                </div>
                <div className="mb-3">
                    <label>Tiempo de preparacion</label>
                    <input type="text" className="form-control" onChange={(e)=>setTiempo(e.target.value)} value={tiempo}/>
                </div>
                <div className="mb-3">
                    <label>Instrucciones</label>
                    <input type="text" className="form-control" onChange={(e)=>setInstrucciones(e.target.value)} value={instrucciones}/>
                </div>
                <div className="mb-3">
                    <label>Informacion nutricional</label>
                    <input type="text" className="form-control" onChange={(e)=>setInformacion(e.target.value)} value={informacion}/>
                </div>
                <div className="mb-3">
                    <label>Categoria</label>
                    <input type="text" className="form-control" onChange={(e)=>setCategoria(e.target.value)} value={categoria}/>
                </div>
                <button type="submit" className="btn btn-primary">Guardar</button>
            </form>
        </div>

    )

}

export default CompCreateRecipes;
