import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import swal from 'sweetalert2';
import { useAuth } from "../AuthContext/AuthContext.js";

const URI = "http://localhost:8000/users/";

const CompEditUsers = () => {

    const {id} = useParams();
    const [alias, setAlias] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();
    const { authState } = useAuth();

    useEffect(() => {
        if (!authState.isAuthenticated) {
            navigate('/');
            return;
        }
    }
    , [authState.isAuthenticated, navigate]);

    //Procedimiento para actualizar usuario

    const update = async (e) => {
        e.preventDefault();
        const user = {
            alias: alias,
            email: email,
            contraseña: password
        }

        const res = await axios.put(URI+id, user);
        navigate('/users');
    }

    useEffect( ()=>{
        const getUserById = async () => {
            const res = await axios.get(URI+id);
            setAlias(res.data.alias);
            setEmail(res.data.email);
            setPassword(res.data.contraseña);
        }
        getUserById();
    },[id])


    return (
        <div>
            <h3>Editar Usuario</h3>
            <form onSubmit={update}>
                <div className="mb-3">
                    <label>Alias</label>
                    <input type="text" className="form-control" onChange={(e)=>setAlias(e.target.value)} value={alias}/>
                </div>
                <div className="mb-3">
                    <label>Email</label>
                    <input type="text" className="form-control" onChange={(e)=>setEmail(e.target.value)} value={email}/>
                </div>
                <div className="mb-3">
                    <label>Contraseña</label>
                    <input type="text" className="form-control" onChange={(e)=>setPassword(e.target.value)} value={password}/>
                </div>
                <button type="submit" className="btn btn-primary">Actualizar</button>
            </form>
        </div>
    )

}

export default CompEditUsers;
